let game = {
	isGameLocked: false,
	firstCardCheck: null,
	secondCardCheck: null,
	flippedCards: 0,
	icons: [
		"bootstrap",
		"css",
		"electron",
		"firebase",
		"html",
		"javascript",
		"jquery",
		"mongo",
		"node",
		"react",
	],
	cards: [],
	createCards: function () {
		game.cards = []

		game.icons.forEach(element => {
			game.cards.push(game.duplicate(element))
		})

		game.cards = game.cards.flatMap(pair => pair)
		game.shuffleCards()
	},
	duplicate: function (original) {
		return [
			{
				id: game.createID(original),
				icon: original,
				flipped: false,
			},
			{
				id: game.createID(original),
				icon: original,
				flipped: false,
			},
		]
	},
	createID: function (tech) {
		return tech + parseInt(Math.random() * 1000)
	},
	shuffleCards: function () {
		let currentIndex = game.cards.length
		let randomIndex = 0

		while (currentIndex !== 0) {
			randomIndex = Math.floor(Math.random() * currentIndex)
			currentIndex--
			;[game.cards[randomIndex], game.cards[currentIndex]] = [
				game.cards[currentIndex],
				game.cards[randomIndex],
			]
		}
		return game.cards
	},
	cardCheck: function (currentCard) {
		let isFlipped = currentCard.flipped === true

		if (isFlipped || this.isGameLocked) {
			return false
		}

		if (!this.firstCardCheck) {
			currentCard.flipped = true
			game.firstCardCheck = currentCard
			return true
		} else {
			currentCard.flipped = true
			game.secondCardCheck = currentCard
			game.isGameLocked = true
			return true
		}
	},
	pairCheck: function () {
		if (!this.firstCardCheck || !this.secondCardCheck) {
			return false
		}
		return (
			this.firstCardCheck.icon ===
			this.secondCardCheck.icon
		)
	},
	clearCheck: function () {
		game.firstCardCheck = null
		game.secondCardCheck = null
		game.isGameLocked = false
	},
	isGameOver: function () {
		this.cards.forEach((card) => {
			if (card.flipped) {
				++this.flippedCards
			}
		})
		if (this.flippedCards == 20) {
			return true
		} else {
			game.flippedCards = 0
			return false
		}
	},
	flipCards: function (card, setIsGameOver, setCards) {
		if (this.cardCheck(card)) {
			if (this.pairCheck()) {
				this.clearCheck()
				if (this.isGameOver()) {
					setIsGameOver(true)
				}
			} else if (this.isGameLocked) {
				setTimeout(() => {
					this.unflipCards()
					this.clearCheck()
					setCards([...this.cards])
				}, 500)
			}
			setCards([...this.cards])
		}	
	},
	unflipCards: function () {
		this.firstCardCheck.flipped = false
		this.secondCardCheck.flipped = false
	}
}

export default game
