import React, { useState, useEffect } from "react"
import GameBoard from "./components/GameBoard.jsx"
import GameOver from './components/GameOver.jsx'
import game from "./game/game.js"

const App = () => {
	const [isGameOver, setIsGameOver] = useState(false)
	const [cards, setCards] = useState([])

	useEffect(() => {
		game.createCards()
		setCards(game.cards)
	}, []);

	function resetGame() {
		setIsGameOver(!isGameOver)
	}
	function flipCard(card) {
		if (game.cardCheck(card)) {
			if (game.pairCheck()) {
				game.clearCheck()
				if (game.isGameOver()) {
					setIsGameOver()
				}
			} else if (game.isGameLocked) {
				setTimeout(() => {
					let firstCardCheckView = game.firstCardCheck
					let secondCardCheckView = game.secondCardCheck

					firstCardCheckView.classList.remove('flip')
					secondCardCheckView.classList.remove('flip')
					game.clearCheck()
				}, 500)
			}
		}
	}

	return (
		<div>
			<GameBoard cards={cards} flipCard={flipCard}></GameBoard>
			<GameOver show={isGameOver} resetGame={resetGame}></GameOver>
		</div>
	)
}

export default App
